  display: inline-block;
  width: 65px;
  padding-top: 2px;
}

sp_widget.script:
// populate the 'data' variable with catalog item, variables, and variable view
(function() {
	options.show_add_cart_button = (options.show_add_cart_button == "true");

	if (options.page) {
		var pageGR = new GlideRecord("sp_page");
		options.page = (pageGR.get(options.page)) ? pageGR.getValue("id") : null;
	}

	if (options.table) {
		var tableGR = new GlideRecord("sys_db_object");
		options.table = (tableGR.get(options.table)) ? tableGR.getValue("name") : null;
	}

	options.url = options.url || "?id={page}&table={table}&sys_id={sys_id}";
	data.showPrices = $sp.showCatalogPrices();
	var m = data.msgs = {};
	m.submitMsg = gs.getMessage("Submit");
	m.submittedMsg = gs.getMessage("Submitted");
	m.createdMsg = gs.getMessage("Created");
	m.trackMsg = gs.getMessage("track using 'Requests' in the header or");
	m.clickMsg = gs.getMessage("click here to view");
	m.dialogTitle = gs.getMessage("Delete Attachment");
	m.dialogMessage = gs.getMessage("Are you sure?");
	m.dialogOK = gs.getMessage("OK");
	m.dialogCancel = gs.getMessage("Cancel");

	var userID = gs.getUser().getID();
	var cart = new SPCart('DEFAULT', userID);

	if (input)
	{
		data.sys_id = input.sys_id;
		if (input.action === "checkout") 
		{
			cart.setSpecialInstructions(input.additionalDetails);
			cart.setRequestedFor(input.cart.requested_for);
			cart.setDeliveryAddress(input.deliverTo);
			var request = cart.placeOrder();
			data.requestData = {
				number: request.getValue("number"),
				table: request.getTableName(),
				sys_id: request.getUniqueValue()
			};
			cart.setSpecialInstructions("");
			cart.setDeliveryAddress("");
		}
	}
	else
	{
		data.sys_id = $sp.getParameter("sys_id") || $sp.getParameter('sl_sys_id');
	}


	data._attachmentGUID = gs.generateGUID();

	data.debug = false; // turn this on to show/hide the div with debug data.
	data.isFav = false; // this is the default flag for favorites functionality
	data.itemID = data.sys_id; // get the item sys id
	data.userID = gs.getUserID(); // get the user id

	// portal can specify a catalog home page
	data.sc_catalog_page = $sp.getDisplayValue("sc_catalog_page") || "sc_home";
	var validatedItem = new GlideappCatalogItem.get(data.sys_id);
	if (!validatedItem.canView())
		return;

	data.sc_cat_item = $sp.getCatalogItem(data.sys_id);
	if (data.sc_cat_item.category) {
		var categoryGR = new GlideRecord('sc_category');
		categoryGR.get(data.sc_cat_item.category);
		data.category = {
			name: categoryGR.getDisplayValue('title'),
			url: '?id=sc_category&sys_id=' + categoryGR.sys_id
		};
	}

	$sp.logStat('Catalog View', data.sc_cat_item.sys_class_name, data.sys_id, data.sc_cat_item.name);	

	data.sys_properties = 
	{
		twostep_checkout: gs.getProperty("glide.sc.checkout.twostep", "false") == 'true'
	};

	data.isFav = checkFav(data.itemID, data.userID); // function checks to see if the item is favorited and returns true/false.

	var cartID = cart.getCartID();
	data.saveCartModal = $sp.getWidget('widget-modal', {embeddedWidgetId: 'sc_save_bundle', embeddedWidgetOptions: {}});

	data.cart = cart.getCartObj();
	data.cartItems = cart.getItems();

})()

if(input.action == 'fav')
{   // if someone favorites
	var itemID = input.sys_id+''; // gotta reset this variable from input as global variables no longer work.
	var userID = gs.getUserID()+'';
	var success = false;
	var isFavorited = checkFav(itemID, userID);

	// debugging to check the variables being passed in server-side
	// console.log('isFavorited: '+isFavorited+' | articleID: '+articleID+' | userID: '+userID);

	if(!isFavorited)
	{ 
		success = addFav(itemID, userID);
		data.isFav = true;
	}
	else if(isFavorited)
	{
		success = delFav(itemID, userID);
		data.isFav = false;
	}
}

function checkFav(cID, uID)
{
	var fStatus = false;
	var currFav = GlideRecord("u_favorites");
		currFav.addQuery('u_catalog', cID);
		currFav.addQuery('u_user', uID);
		currFav.query();

	if(currFav.getRowCount() > 0)
	{
		fStatus = true;
	}

	data.status = "checked";
	return fStatus;
}

function addFav(cID, uID)
{
	var success = false;
	var addFavKb = GlideRecord("u_favorites"); 
	if(addFavKb.isValid())
	{
		addFavKb.initialize();
		addFavKb.u_catalog = cID+''; 
		addFavKb.u_user = uID+''; 
		addFavKb.insert();
		success = true;
	}
	data.status = "added";


	// debugging to check the variables being passed in server-side to function
	// console.log('[addFav] articleID: '+articleID+' | userID: '+userID+' | success: '+success);

	return success;
}

function delFav(cID, uID)
{
	var success = false;
	var delFavKb = GlideRecord("u_favorites"); 
	if(delFavKb.isValid())
	{
		delFavKb.addQuery("u_catalog", cID); 
		delFavKb.addQuery("u_user", uID); 
		delFavKb.query(); 
		delFavKb.next();
		delFavKb.deleteRecord();
		success = true;
	}
	data.status = "deleted";

	// debugging to check the variables being passed in server-side to function
	// console.log('[delFav] articleID: '+articleID+' | userID: '+userID+' | success: '+success);

	return success;
}
